import React from "react";
import "./BackDrop.css";



const backdrop =(props)=>{
    function handleEscapeKeypress(e){
        console.log(e.keyCode)
        if(e.keyCode === 27){
          return  props.clicked();
        }
    }
   return( props.show === true ? <div className="Backdrop" onClick={props.clicked} onKeyPress={handleEscapeKeypress}></div> :null)
}
export default backdrop;
