import React ,{Component} from "react";
import * as Globals from '../Helpers/Global/Global';
import ExpandCollapseBtn from "../Accordion/ExpandCollapseBtn/ExpandCollapseBtn";
import AccordionTab from "../Accordion/AccordionTab/AccordionTab";
import "../Accordion/Accordion.css";

class Accordion extends Component{

    state={
        componentData :  ""
    }
    
render(){
    
    this.setState({
        componentData:this.props.compData
    })
    //console.log(this.props.compData.AccordionHeadline);

    let heading = null;
    let accordionTabsData = {...this.state.componentData}
    
   
    return(      
    <section className="accordion clear-both @accordwithDesc @Model.themeClass @Model.bgColor @Model.sectionpaddingClass top-parent-element" data-grid="container" id="@Model.componentId" data-tag="tagging">
    <div className="accordion-container pb-0 ">
        <div className="xs-col-24-24 s-col-24-24 pl-0 accordion-headline-bar no-padding">    
            <div className ="headline-holder">
                {heading}
                <ExpandCollapseBtn ExpandText="Expand All" 
                               CollapseText="Collapsea All"
                               ariaLabelExpand="Click to collapse"
                               ariaLabelCollapse="Click to expand" />
            </div>           
            

           {/* if(Model.accordianDescription != null && Model.accordianDescription != "")
           {           
             //   @Html.Raw(Model.accordianDescription);          
           } */}
            
        </div>
        <div className="accordion-main-panel xs-col-24-24 s-col-24-24 pl-0 no-padding">
          
           
        </div>
        {/* if (Model.footnotes != "")
        { */}
            <div className="accordion-foot-notes xs-col-24-24 s-col-24-24 pb-1">
        <p className="c-paragraph-3" style={{display: "none"}}></p>
            </div>
        {/* } */}

    </div>
</section>
    )
    }
    }
    

export default Accordion;
