import React from "react";
import "./BackDrop.css";
function handleEscapeKeypress(e){
    if(e.keyCode === 27){
      return  props.clicked();
    }
}


const backdrop =(props)=>(
    props.show === true ? <div className="Backdrop" onClick={props.clicked} onKeyPress={handleEscapeKeypress}></div> :null
)
export default backdrop;
